INSERT INTO `hf_mall_goods` VALUES ('3371', '35', '53', null, '0', '205', '0', '阿克苏冰糖心苹果', null, '1', '', '新疆土特产', '118.00', '0.00', '[{\"parameter_name\":\"\",\"child_value\":[{\"parameter_name\":\"\",\"child_parameter_name\":\"\",\"equivalence\":\"\",\"Inventory\":\"\"}]}]', '0', '0', '8KG', '', '', '', '0', '0', '0', '[]', '0', '/Upload/goods/2017-01-17_155953.png', '10', '0', '<p><img src=\"EditorUpload/image/20170117/1484639941709557.jpg\" title=\"1484639941709557.jpg\"/></p><p>&nbsp; &nbsp; 阿克苏冰糖心苹果主要产地位于新疆阿克苏市，位于天山托木尔峰南麓，苹果为扁平形，平均果重150克，果面光滑细腻、色泽光亮自然，皮薄肉厚、质地较密、味甜汁多、含糖量高。因为阿克苏冬季寒冷，所以果品生长期病虫害发生极少，昼夜温差大、光照充足，加上用无污染的冰川雪融河流——柯柯牙河浇灌、沙性土壤栽培、高海拔的生长环境，使阿克苏苹果的果核部分糖分堆积成透明状，形成了世界上独一无二的“冰糖心”。阿克苏苹果采摘时间严格控制在每年的10月25日之后，较长的生长期让阿克苏苹果更多的汲取了大自然的天地精华，形成了无与伦比的独特品位，被称为新疆的“水果皇后”，更以其独特的“冰糖心”而饮誉海外</p><p><img src=\"EditorUpload/image/20170117/1484639951589139.jpg\" title=\"1484639951589139.jpg\"/></p><p><img src=\"EditorUpload/image/20170117/1484639960903078.jpg\" title=\"1484639960903078.jpg\"/></p><p><img src=\"EditorUpload/image/20170117/1484639964350856.jpg\" title=\"1484639964350856.jpg\"/></p><p><img src=\"EditorUpload/image/20170117/1484639967980342.jpg\" title=\"1484639967980342.jpg\"/></p><p><br/></p>', '2017-01-17 22:25:26', '2017-01-17 22:25:23', '0', '1', null, null, null, null, null, null, null, null, null);
