INSERT INTO `hf_mall_goods` VALUES ('3374', '35', '54', null, '0', '204', '0', '江泓自生鱼娃娃鱼', null, '1', '', '江泓自生鱼', '1980.00', '0.00', '[{\"parameter_name\":\"3斤——3.5斤\",\"child_value\":[{\"parameter_name\":\"3斤——3.5斤\",\"child_parameter_name\":\"子二代娃娃鱼\",\"equivalence\":\"类野生养殖娃娃鱼\",\"Inventory\":\"10\"}]}]', '0', '0', '条', '', '', '', '0', '0', '0', '[{\"bannerPic\":\"\\/Upload\\/goods\\/2017-01-18_184951.png\"}]', '0', '/Upload/goods/2017-01-18_184951.png', '0', '0', '<p><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">娃娃鱼又名大鲵，是一种集保健、药用、美容为一体的珍贵物种，其肉质细嫩、味道鲜美，含有优质蛋白质、丰富的氨基酸和微量元素，营养价值极高，被誉为</span><span style=\"font-size:19px;font-family:&#39;Arial&#39;,sans-serif;color:#333333;background:white\">“</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">水中人参</span><span style=\"font-size:19px;font-family:&#39;Arial&#39;,sans-serif;color:#333333;background:white\">”</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">。中医认为，大鲵性甘平味淡，有补气、养血、益智、滋补、强壮之功效，主治神经衰弱、贫血、痢疾、疟疾等，用于病后、产后身体虚弱，肾虚阴亏，肺痨咯血等。现代医学认为，经常食用大鲵可聪明益智延缓衰老提高造血和免疫功能，对防止心脑血管系统疾病，恶性贫血和恶性肿瘤有很好的效果。</span></p><p style=\"text-align: center;\"><img src=\"EditorUpload/image/20170117/1484643990363574.jpg\" title=\"1484643990363574.jpg\" width=\"550\" height=\"584\"/></p><p style=\"text-align: center;\"><img src=\"EditorUpload/image/20170117/1484643990406501.jpg\" title=\"1484643990406501.jpg\" width=\"550\" height=\"591\"/></p><p><span style=\"font-size:19px;font-family:宋体\">江泓自生鱼推出的“名流”系列产品——娃娃鱼在水质优良的仿生态环境中人工养殖，拥有非凡的品质和绝佳的口感。</span></p><p><span style=\"font-size:19px;font-family:宋体\">江泓自生鱼每批次水样、鱼样都委托重庆农垦农产品质量安全检验检测站检测；从捕捞、运输、宰杀、包装等全过程受到重庆市水产科学研究所监督，消费者购买的每个产品都有质量检测报告。</span></p><p><span style=\"font-size:19px;font-family:宋体\"></span></p><p style=\"text-align: center;\"><img src=\"EditorUpload/image/20170117/1484644241149773.jpg\" title=\"1484644241149773.jpg\" width=\"552\" height=\"477\"/></p><p style=\"text-align: center;\"><img src=\"