INSERT INTO `hf_shop_store` VALUES ('163', '368', '依萝香', '依萝香', null, '/', '0', null, 'F2', 'L2-31', null, '49', '', null, null, null, '9：30-21：30', '15982640468', null, '                                重庆爱萝床上用品有限公司，于2013年重磅推出“依萝香”家纺品牌。公司创始人徐祖刚先生自1996年开始从床上用品的终端销售做起，勤勉踏实，一步一个脚印，不断总结行业经验，逐渐发展成长，已成为集产品研发、生产、销售及销售代理于一体的综合性家纺企业。目前公司在生产厂区有3000余㎡物流配送中心、8000余㎡库房、2000㎡展示中心、在寸土寸金的朝天门综合市场圣鹏国际家纺城设立600㎡销售大厅，以黑马的态势屹立于重庆以及遍布全国区域家纺市场。产品依托与中国最大家纺基地的良好伙伴关系，“依萝香”产品紧趋当今流行时尚，结合全国南北区域消费习惯和审美特点，以“创造舒适休闲的健康生活”为理念，不断研发生产出更适合全国市场的床上用品，先后开发了经典、时尚休闲、婚庆、童趣、韩版等五大系列风格，套件、单件、被子、枕芯、靠垫、毯类、床垫套、沙发巾、家居饰品、卫浴、夏令用品、酒店用品等十余系列类别，数千个品种。 作为本土企业，大西南的山、水、人文情结，无不潜移默化地融入“依萝香”品牌产品的品性之中，自然地形成了强大生命力“时尚家纺”的鲜明定位。而完美的品质、完善的服务、工薪的价格则是“依萝香”长立于市场而不衰的致胜法宝。 不断完善的企业管理及不断提升的产品品质。\r\n                                                                                      ', '', '', '', null, '2205265444', null, '72', null, '2015-6-27', '/Upload/logo/2017-01-20_210939.jpg', '/Upload/logo/2017-01-20_2109391.jpg', null, '营业中', '1', '0', '2017-01-20 21:09:52', '1', '2');
